mapper.select:
select * from ( select row_number() over (order by product_no desc) as rn, product_no as no, product_category_no as categoryNo, product_mid_category_no as categoryMidNo, product_low_category_no as categoryLowNo, product_color_no as colorNo, product_brand_name as productBrandName, product_cd as productCd, product_name as productName, product_info as info, product_deleted as deleted, product_hit_cnt as hitCnt, product_created_date as createdDate, product_photo_filename as photoFilename from latte_products
PRODUCT_CATEGORY_NO = #{categoryNo}
PRODUCT_MID_CATEGORY_NO = #{categoryNo}
PRODUCT_LOW_CATEGORY_NO = #{categoryNo}
PRODUCT_CATEGORY_NO = #{categoryNo}
)
rn >= #{begin} and rn <= #{end}

mapper.insert:

select latte_product_no_seq.nextval from dual

insert into latte_products
	(
	  PRODUCT_NO
	, PRODUCT_CATEGORY_NO
	, PRODUCT_MID_CATEGORY_NO
	, PRODUCT_LOW_CATEGORY_NO
	, PRODUCT_COLOR_NO
	, PRODUCT_BRAND_NAME
	, PRODUCT_CD
	, PRODUCT_NAME
	, PRODUCT_INFO
	, PRODUCT_HIT_CNT
	, PRODUCT_PHOTO_FILENAME
	)
values
	(
	  #{no}
	, #{categoryNo}
	, #{categoryMidNo}
	, #{categoryLowNo}
	, #{colorNo}
	, #{productBrandName}
	, #{productCd}
	, #{productName}
	, #{info}
	, #{hitCnt}
	, #{photoFilename})

INSERT INTO LATTE_PRODUCT_DETAILS
	(
	  PRODUCT_CD
    , PRODUCT_NAME
    , PRODUCT_BRAND_NAME
    , PRODUCT_MALL_NO
    , PRODUCT_PRICE
    , PRODUCT_FREE_DELIVERY
    , PRODUCT_URL
	)
VALUES
	(
	  #{detailCd}
	, #{detailName}
	, #{detailBrandName}
	, #{mallNo}
	, #{price}
	, #{freeDelivery}
	, #{url}
	)

mapper.update:
update latte_products
set
	product_hit_cnt = #{hitCnt}
where
	product_no = #{no}
